defmodule Advent.Two do
  @numpad [
    [nil, nil, "1", nil, nil],
    [nil, "2", "3", "4", nil],
    ["5", "6", "7", "8", "9"],
    [nil, "A", "B", "C", nil],
    [nil, nil, "D", nil, nil]
  ]
  @start_key {0, 2}
  @code ""
  @instructions "\\\\"

  def run() do
    result = String.split(@instructions, "\n")
    |> read_instructions(@start_key, @code)

    IO.puts result
  end

  def read_instructions([], _, code), do: code
  def read_instructions([row | tail], pos, code) do
    {pos, code} = String.split(row, "")
    |> read_row(pos, code)

    read_instructions(tail, pos, code)
  end

  def read_row([], {x, y}, code) do
    key = @numpad
    |> Enum.at(y)
    |> Enum.at(x)

    {{x, y}, code <> key}
  end
  def read_row([direction | tail], pos, code) do
    pos = update_location(direction, pos)
    read_row(tail, pos, code)
  end

  def update_location("U", {x, y}) do
    case valid_key(x, y - 1) do
      true  -> {x, y - 1}
      false -> {x, y}
    end
  end
  def update_location("D", {x, y}) do
    case valid_key(x, y + 1) do
      true  -> {x, y + 1}
      false -> {x, y}
    end
  end
  def update_location("L", {x, y}) do
    case valid_key(x - 1, y) do
      true  -> {x - 1, y}
      false -> {x, y}
    end
  end
  def update_location("R", {x, y}) do
    case valid_key(x + 1, y) do
      true  -> {x + 1, y}
      false -> {x, y}
    end
  end
  def update_location("", pos), do: pos

  def valid_key(x, y) when (x >= 0 and x < 5 and y >= 0 and y < 5) do
    key = @numpad
    |> Enum.at(y)
    |> Enum.at(x)

    case key do
      nil -> false
      _   -> true
    end
  end
  def valid_key(_, _), do: false
end
